client/src/pages/workflow-detail.tsx.content:
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { useRoute, Link } from "wouter";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { ScrollArea } from "@/components/ui/scroll-area";
import { ArrowLeft, Play, Edit, Trash2, Copy } from "lucide-react";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import type { Workflow } from "@shared/schema";

export default function WorkflowDetail() {
  const [, params] = useRoute<{ id: string }>("/workflows/:id");
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const { data: workflow, isLoading } = useQuery<Workflow>({
    queryKey: [`/api/workflows/${params?.id}`],
    enabled: !!params?.id,
  });

  const executeWorkflowMutation = useMutation({
    mutationFn: async () => {
      return apiRequest('POST', `/api/workflows/${params?.id}/execute`, undefined);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/executions'] });
      toast({
        title: "Workflow started",
        description: "Your automation is now running.",
      });
    },
    onError: (error: Error) => {
      toast({
        title: "Failed to start workflow",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  if (isLoading) {
    return (
      <div className="flex flex-col gap-6 p-8">
        <div className="h-8 w-48 bg-muted animate-pulse rounded" />
        <div className="grid gap-6 lg:grid-cols-3">
          <div className="lg:col-span-2 h-96 bg-muted animate-pulse rounded-lg" />
          <div className="h-96 bg-muted animate-pulse rounded-lg" />
        </div>
      </div>
    );
  }

  if (!workflow) {
    return (
      <div className="flex flex-col items-center justify-center min-h-[60vh] gap-4">
        <h2 className="text-2xl font-bold">Workflow not found</h2>
        <Link href="/workflows">
          <Button>Back to Workflows</Button>
        </Link>
      </div>
    );
  }

  return (
    <div className="flex flex-col gap-6 p-8">
      <div className="flex items-center gap-4">
        <Link href="/workflows">
          <Button variant="ghost" size="icon" data-testid="button-back">
            <ArrowLeft className="h-4 w-4" />
          </Button>
        </Link>
        <div className="flex-1">
          <h1 className="text-3xl font-bold mb-1">{workflow.name}</h1>
          <p className="text-sm text-muted-foreground">{workflow.description || "No description"}</p>
        </div>
        <Button
          onClick={() => executeWorkflowMutation.mutate()}
          disabled={executeWorkflowMutation.isPending}
          className="gap-2"
          data-testid="button-execute-workflow"
        >
          <Play className="h-4 w-4" />
          {executeWorkflowMutation.isPending ? "Starting..." : "Run Workflow"}
        </Button>
      </div>

      <div className="grid gap-6 lg:grid-cols-3">
        <div className="lg:col-span-2 space-y-6">
          <Card>
            <CardHeader>
              <CardTitle>Workflow Steps</CardTitle>
              <CardDescription>{workflow.steps.length} automation steps</CardDescription>
            </CardHeader>
            <CardContent>
              <ScrollArea className="h-96">
                <div className="space-y-3">
                  {workflow.steps.map((step, index) => (
                    <div
                      key={step.id}
                      className="flex items-start gap-3 p-4 rounded-lg border bg-card"
                      data-testid={`step-${index}`}
                    >
                      <div className="flex h-8 w-8 items-center justify-center rounded-full bg-primary text-primary-foreground text-sm font-medium flex-shrink-0">
                        {step.order + 1}
                      </div>
                      <div className="flex-1 min-w-0">
                        <div className="flex items-center gap-2 mb-2">
                          <Badge variant="outline">{step.type}</Badge>
                        </div>
                        <p className="text-sm mb-2">{step.description}</p>
                        {Object.keys(step.config).length > 0 && (
                          <div className="mt-2 p-2 rounded bg-muted/50">
                            <pre className="text-xs font-mono overflow-x-auto">
                              {JSON.stringify(step.config, null, 2)}
                            </pre>
                          </div>
                        )}
                      </div>
                    </div>
                  ))}
                </div>
              </ScrollArea>
            </CardContent>
          </Card>
        </div>

        <div className="space-y-6">
          <Card>
            <CardHeader>
              <CardTitle>Workflow Settings</CardTitle>
            </CardHeader>
            <CardContent className="space-y-4">
              <div>
                <p className="text-sm text-muted-foreground mb-1">Execution Mode</p>
                <Badge>{workflow.executionMode}</Badge>
              </div>
              <div>
                <p className="text-sm text-muted-foreground mb-1">Browser Mode</p>
                <Badge variant="outline">
                  {workflow.browserConfig?.headless ? "Headless" : "Headful"}
                </Badge>
              </div>
              {workflow.browserConfig?.viewport && (
                <div>
                  <p className="text-sm text-muted-foreground mb-1">Viewport</p>
                  <p className="text-sm font-mono">
                    {workflow.browserConfig.viewport.width} x {workflow.browserConfig.viewport.height}
                  </p>
                </div>
              )}
              <div>
                <p className="text-sm text-muted-foreground mb-1">Created</p>
                <p className="text-sm">{new Date(workflow.createdAt).toLocaleString()}</p>
              </div>
              <div>
                <p className="text-sm text-muted-foreground mb-1">Updated</p>
                <p className="text-sm">{new Date(workflow.updatedAt).toLocaleString()}</p>
              </div>
            </CardContent>
          </Card>

          <Card>
            <CardHeader>
              <CardTitle>Actions</CardTitle>
            </CardHeader>
            <CardContent className="space-y-2">
              <Button variant="outline" className="w-full gap-2 justify-start" data-testid="button-edit">
                <Edit className="h-4 w-4" />
                Edit Workflow
              </Button>
              <Button variant="outline" className="w-full gap-2 justify-start" data-testid="button-duplicate">
                <Copy className="h-4 w-4" />
                Duplicate
              </Button>
              <Button variant="destructive" className="w-full gap-2 justify-start" data-testid="button-delete">
                <Trash2 className="h-4 w-4" />
                Delete Workflow
              </Button>
            </CardContent>
          </Card>
        </div>
      </div>
    </div>
  );
}

server/lib/custom-llm-client.ts.content:
import { spawn } from "child_process";
import https from "https";

interface LLMMessage {
  role: string;
  content: string | any;
}

interface LLMResponse {
  id: string;
  object: string;
  created: number;
  model: string;
  choices: Array<{
    index: number;
    message: {
      role: string;
      content: string;
      tool_calls?: any[];
    };
    finish_reason: string;
  }>;
  usage: {
    prompt_tokens: number;
    completion_tokens: number;
    total_tokens: number;
  };
}

interface CustomLLMConfig {
  apiEndpoint: string;
  apiKey?: string | null;
  actualModelName?: string;
}

function logger(params: { category: string; message: string; level: number }) {
  const prefix = params.level === 0 ? '[ERROR]' : '[INFO]';
  console.log(`${prefix} [${params.category}] ${params.message}`);
}

export class CustomLLMClient {
  private apiEndpoint: string;
  private apiKey: string | null;
  private actualModelName: string;
  private oauthToken: string | null = null;
  private baseURL: string | null = null;

  constructor(config: CustomLLMConfig) {
    this.apiEndpoint = config.apiEndpoint;
    this.apiKey = config.apiKey || null;
    this.actualModelName = config.actualModelName || "gpt-4-1-2025-04-14-eastus-dz";
  }

  async createChatCompletion(options: {
    messages: LLMMessage[];
    temperature?: number;
    maxTokens?: number;
    response_model?: any;
  }): Promise<LLMResponse | any> {
    const { messages, temperature = 0.7, maxTokens = 4096, response_model } = options;
    let retries = 3;

    // Fetch OAuth token if not yet fetched
    if (!this.oauthToken) {
      try {
        const tokenResponse = await this.fetchOAuthConfig();
        this.oauthToken = tokenResponse.access_token;
        if (tokenResponse.baseURL) {
          this.baseURL = tokenResponse.baseURL;
        }
        logger({
          category: "custom-llm",
          message: "Fetched OAuth token",
          level: 1,
        });
      } catch (err) {
        logger({
          category: "custom-llm",
          message: `Failed to fetch OAuth token: ${err instanceof Error ? err.message : String(err)}`,
          level: 0,
        });
        throw err;
      }
    }

    const makeRequest = (): Promise<any> =>
      new Promise(async (resolve, reject) => {
        const requestPayload = {
          model: this.actualModelName,
          messages: messages.map((msg) => ({
            role: msg.role,
            content: typeof msg.content === "string" ? msg.content : JSON.stringify(msg.content),
          })),
          temperature,
          max_tokens: maxTokens,
        };
        const requestBody = JSON.stringify(requestPayload);

        try {
          const endpoint = this.baseURL ? `${this.baseURL}/chat/completions` : this.apiEndpoint;
          const url = new URL(endpoint);
          const reqOptions = {
            hostname: url.hostname,
            port: url.port || 443,
            path: url.pathname + url.search,
            method: "POST",
            headers: {
              "Content-Type": "application/json",
              Authorization: `Bearer ${this.oauthToken}`,
              "Content-Length": Buffer.byteLength(requestBody),
            },
            rejectUnauthorized: false,
          };
          let responseData = "";
          const req = https.request(reqOptions, (res) => {
            res.on("data", (chunk) => {
              responseData += chunk;
            });
            res.on("end", () => {
              if (res.statusCode && res.statusCode >= 200 && res.statusCode < 300) {
                try {
                  const data = JSON.parse(responseData);
                  resolve(data);
                } catch (e) {
                  reject(new Error(`Failed to parse response: ${responseData}`));
                }
              } else if (res.statusCode === 401) {
                this.oauthToken = null;
                reject(new Error(`Unauthorized – token expired or invalid: ${responseData}`));
              } else {
                reject(
                  new Error(`API request failed with status ${res.statusCode}: ${responseData}`)
                );
              }
            });
          });
          req.on("error", (err) => reject(err));
          req.write(requestBody);
          req.end();
        } catch (err) {
          reject(err);
        }
      });

    while (retries > 0) {
      try {
        const data = await makeRequest();
        const messageContent = data?.choices?.[0]?.message?.content || data.content || "";
        const toolCalls =
          Array.isArray(data?.choices?.[0]?.message?.tool_calls) &&
          data.choices[0].message.tool_calls.length > 0
            ? data.choices[0].message.tool_calls
            : [];
        const messageObj: any = { role: "assistant", content: messageContent };
        if (toolCalls.length > 0) messageObj.tool_calls = toolCalls;

        const formattedResponse: LLMResponse = {
          id: data.id || "custom-" + Date.now(),
          object: "chat.completion",
          created: Math.floor(Date.now() / 1000),
          model: this.actualModelName,
          choices: [
            {
              index: 0,
              message: messageObj,
              finish_reason: data?.choices?.[0]?.finish_reason || "stop",
            },
          ],
          usage: {
            prompt_tokens: data?.usage?.prompt_tokens || 0,
            completion_tokens: data?.usage?.completion_tokens || 0,
            total_tokens: data?.usage?.total_tokens || 0,
          },
        };

        // Optional structured parsing if requested by response_model
        if (response_model) {
          logger({
            category: "custom-llm",
            message: `Response model requested, parsing: ${messageContent}`,
            level: 1,
          });
          try {
            const parsedData = JSON.parse(messageContent);
            logger({
              category: "custom-llm",
              message: `Parsed ${JSON.stringify(parsedData, null, 2)}`,
              level: 1,
            });

            if (parsedData && typeof parsedData === "object") {
              // Handle response with element object (e.g., { method: "click", element: { id: ["0-13"]}})
              if (parsedData.elementId || (parsedData.element && parsedData.method)) {
                // Extract element ID from either 'id' or 'path' field
                const rawId = parsedData.elementId || parsedData.element.id || "0-1";
                const elementId =
                  typeof rawId === "string"
                    ? rawId.replace(/[\[\]\s'"]/g, "")
                    : String(rawId);
                const normalizedData = {
                  elementId,
                  method: parsedData.action || parsedData.method,
                  arguments: parsedData.argument
                    ? [parsedData.argument]
                    : parsedData.arguments || [],
                };
                logger({
                  category: "custom-llm",
                  message: `Normalized data (element/action): ${JSON.stringify(
                    normalizedData,
                    null,
                    2
                  )}`,
                  level: 1,
                });

                return {
                  ...normalizedData,
                  usage: formattedResponse.usage,
                };
              }
            }

            // Other structured responses, return data and usage
            return { ...parsedData, usage: formattedResponse.usage };
          } catch (e) {
            logger({
              category: "custom-llm",
              message: `Failed to parse JSON: ${e instanceof Error ? e.message : String(e)}`,
              level: 1,
            });
            // Try JSON match
            const jsonMatch = messageContent.match(/[\[{][\s\S]*[\]}]/);
            if (jsonMatch) {
              try {
                const extractedJson = JSON.parse(jsonMatch[0]);
                logger({
                  category: "custom-llm",
                  message: `Extracted JSON: ${JSON.stringify(extractedJson, null, 2)}`,
                  level: 1,
                });
                if (extractedJson.element && extractedJson.action) {
                  const elementId = extractedJson.element.id?.replace(/[\[\]\s'"]/g, "") || "0-1";
                  const normalizedData = {
                    elementId,
                    method: extractedJson.action,
                    arguments: extractedJson.argument
                      ? [extractedJson.argument]
                      : extractedJson.arguments || [],
                  };
                  logger({
                    category: "custom-llm",
                    message: `Normalized data (extracted): ${JSON.stringify(
                      normalizedData,
                      null,
                      2
                    )}`,
                    level: 1,
                  });
                  return {
                    ...normalizedData,
                    usage: formattedResponse.usage,
                  };
                }
                return { ...extractedJson, usage: formattedResponse.usage };
              } catch (e2) {
                logger({
                  category: "custom-llm",
                  message: `Extraction also failed: ${e2 instanceof Error ? e2.message : String(e2)}`,
                  level: 1,
                });
              }
            }
            return formattedResponse;
          }
        }

        return formattedResponse;
      } catch (error: any) {
        if (error.message.includes("Unauthorized")) {
          retries--;
          if (retries === 0) throw error;
          try {
            const tokenResponse = await this.fetchOAuthConfig();
            this.oauthToken = tokenResponse.access_token;
            logger({
              category: "custom-llm",
              message: "Refreshed OAuth token after unauthorized error",
              level: 1,
            });
          } catch (tokenError) {
            logger({
              category: "custom-llm",
              message: `Failed to refresh OAuth token: ${
                tokenError instanceof Error ? tokenError.message : String(tokenError)
              }`,
              level: 0,
            });
            throw tokenError;
          }
        } else {
          logger({
            category: "custom-llm",
            message: `Error: ${error instanceof Error ? error.message : String(error)}`,
            level: 0,
          });
          if (retries <= 0) throw error;
          retries--;
        }
      }
    }

    throw new Error('Max retries exceeded');
  }

  async fetchOAuthConfig(): Promise<any> {
    return new Promise((resolve, reject) => {
      const pythonProcess = spawn("python", ["fetch_token.py"]);
      let output = "";
      let errorOutput = "";
      pythonProcess.stdout.on("data", (data) => {
        output += data.toString();
      });
      pythonProcess.stderr.on("data", (data) => {
        errorOutput += data.toString();
      });
      pythonProcess.on("close", (code) => {
        if (code !== 0) {
          reject(new Error(`Python script failed: ${errorOutput}`));
        } else {
          try {
            const result = JSON.parse(output.trim());
            if (result.error) {
              reject(new Error(`OAuth error: ${result.error}`));
            } else {
              resolve(result);
            }
          } catch (e) {
            reject(new Error(`Failed to parse OAuth response: ${output}`));
          }
        }
      });
    });
  }
}

client/src/hooks/use-websocket.ts.content:
import { useEffect, useRef } from "react";
import { io, type Socket } from "socket.io-client";
import { useQueryClient } from "@tanstack/react-query";

interface WebSocketMessage {
  type: string;
  executionId?: string;
  workflowId?: string;
  log?: any;
  currentStep?: number;
  totalSteps?: number;
  stepDescription?: string;
  results?: any;
  duration?: number;
  error?: string;
}

export function useWebSocket() {
  const socketRef = useRef<Socket | null>(null);
  const queryClient = useQueryClient();

  useEffect(() => {
    // Connect to WebSocket server
    const socket = io(window.location.origin, {
      path: "/socket.io",
      transports: ["websocket", "polling"],
    });

    socketRef.current = socket;

    socket.on("connect", () => {
      console.log("[WebSocket] Connected:", socket.id);
    });

    socket.on("disconnect", () => {
      console.log("[WebSocket] Disconnected");
    });

    // Handle execution started
    socket.on("execution:started", (data: WebSocketMessage) => {
      console.log("[WebSocket] Execution started:", data);
      queryClient.invalidateQueries({ queryKey: ["/api/executions"] });
      queryClient.invalidateQueries({ queryKey: [`/api/executions/${data.executionId}`] });
    });

    // Handle execution logs
    socket.on("execution:log", (data: WebSocketMessage) => {
      console.log("[WebSocket] Log:", data.log);
      // Invalidate execution to get updated logs
      queryClient.invalidateQueries({ queryKey: [`/api/executions/${data.executionId}`] });
    });

    // Handle execution progress
    socket.on("execution:progress", (data: WebSocketMessage) => {
      const progress = data.totalSteps ? Math.round((data.currentStep! / data.totalSteps) * 100) : 0;
      console.log(`[WebSocket] Progress: ${progress}% (${data.currentStep}/${data.totalSteps}) - ${data.stepDescription}`);
      queryClient.invalidateQueries({ queryKey: [`/api/executions/${data.executionId}`] });
    });

    // Handle execution completed
    socket.on("execution:completed", (data: WebSocketMessage) => {
      console.log("[WebSocket] Execution completed:", data);
      queryClient.invalidateQueries({ queryKey: ["/api/executions"] });
      queryClient.invalidateQueries({ queryKey: [`/api/executions/${data.executionId}`] });
    });

    // Handle execution failed
    socket.on("execution:failed", (data: WebSocketMessage) => {
      console.error("[WebSocket] Execution failed:", data);
      queryClient.invalidateQueries({ queryKey: ["/api/executions"] });
      queryClient.invalidateQueries({ queryKey: [`/api/executions/${data.executionId}`] });
    });

    return () => {
      socket.disconnect();
    };
  }, [queryClient]);

  return socketRef.current;
}

